import React, { useState, useEffect } from "react";
import {
  Search,
  User,
  LogOut,
  Layers,
  BookOpen,
  Award,
  Clock,
  LayoutDashboard,
  Menu,
  X,
  ChevronRight,
} from "lucide-react";

const Dashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [activeCourse, setActiveCourse] = useState(null); // for modal
  const [loading, setLoading] = useState(true);

  const [user] = useState({
    username: "John Doe",
    email: "john@example.com",
    enrolledCourses: 3,
    completedCourses: 1,
    totalPoints: 850,
  });

  const categories = ["All", "Development", "Data Science", "Design", "Marketing"];

  // Simulate API fetch
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    setTimeout(() => {
      setCourses(FAKE_COURSES);
      setLoading(false);
    }, 800);
  }, []);

  const filteredCourses = courses.filter((course) => {
    const matchesSearch =
      course.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      course.instructor.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesCategory = selectedCategory === "All" || course.category === selectedCategory;

    return matchesSearch && matchesCategory;
  });

  const handleLogout = () => alert("Logout triggered");

  return (
    <div className="min-h-screen flex bg-gray-50">
      {/* Sidebar */}
      <aside
        className={`fixed z-50 lg:relative lg:translate-x-0 transform ${
          sidebarOpen ? "translate-x-0" : "-translate-x-full"
        } transition-all duration-300 w-64 bg-white shadow-xl h-screen`}
      >
        <div className="p-6 border-b">
          <h1 className="text-2xl font-bold text-indigo-600 flex items-center gap-2">
            <BookOpen /> LearnHub
          </h1>
        </div>

        <nav className="p-4 space-y-3">
          <SidebarItem icon={<LayoutDashboard />} text="Dashboard" active />
          <SidebarItem icon={<Layers />} text="My Courses" />
          <SidebarItem icon={<Award />} text="Achievements" />
          <SidebarItem icon={<Clock />} text="Learning History" />
        </nav>
      </aside>

      {/* Overlay for mobile */}
      {sidebarOpen && (
        <div
          onClick={() => setSidebarOpen(false)}
          className="fixed inset-0 bg-black/30 z-40 lg:hidden"
        ></div>
      )}

      {/* Main Content */}
      <main className="flex-1 p-6 lg:p-10">
        {/* Topbar */}
        <div className="flex justify-between items-center mb-6">
          <button
            onClick={() => setSidebarOpen(true)}
            className="lg:hidden p-2 bg-white rounded-lg shadow"
          >
            <Menu />
          </button>

          <div className="flex items-center gap-4">
            <div className="px-4 py-2 bg-gray-100 rounded-full flex items-center gap-2">
              <User size={20} />
              <span className="font-medium">{user.username}</span>
            </div>

            <button
              onClick={handleLogout}
              className="flex items-center gap-2 bg-red-500 text-white px-4 py-2 rounded-full hover:bg-red-600 transition"
            >
              <LogOut size={18} /> Logout
            </button>
          </div>
        </div>

        {/* Stats */}
        <StatsRow user={user} />

        {/* Search + Filters */}
        <SearchAndFilter
          searchQuery={searchQuery}
          setSearchQuery={setSearchQuery}
          categories={categories}
          selectedCategory={selectedCategory}
          setSelectedCategory={setSelectedCategory}
        />

        {/* Courses Grid */}
        <h2 className="text-2xl font-bold text-gray-700 mb-6">Courses</h2>

        {loading ? (
          <CourseSkeletonGrid />
        ) : (
          <CourseGrid courses={filteredCourses} setActiveCourse={setActiveCourse} />
        )}
      </main>

      {/* Course Modal */}
      {activeCourse && <CourseModal course={activeCourse} onClose={() => setActiveCourse(null)} />}
    </div>
  );
};

/* ----------------------- REUSABLE COMPONENTS ---------------------------- */

const SidebarItem = ({ icon, text, active }) => (
  <div
    className={`flex items-center gap-3 px-4 py-3 rounded-lg cursor-pointer transition ${
      active
        ? "bg-indigo-600 text-white shadow"
        : "text-gray-700 hover:bg-gray-100 hover:shadow"
    }`}
  >
    {icon}
    <span className="font-medium">{text}</span>
  </div>
);

const StatsRow = ({ user }) => (
  <div className="grid grid-cols-2 md:grid-cols-4 gap-6 mb-10">
    <StatsCard title="Enrolled Courses" value={user.enrolledCourses} icon={<BookOpen />} color="blue" />
    <StatsCard title="Completed" value={user.completedCourses} icon={<Award />} color="green" />
    <StatsCard title="Total Points" value={user.totalPoints} icon="üìä" color="purple" />
    <StatsCard title="Learning Hours" value="47h" icon={<Clock />} color="orange" />
  </div>
);

const StatsCard = ({ title, value, icon, color }) => (
  <div className={`bg-white shadow-lg p-6 rounded-xl border-l-4 border-${color}-500`}>
    <div className="flex justify-between">
      <div>
        <p className="text-gray-500 text-sm font-medium">{title}</p>
        <p className="text-3xl font-bold text-gray-800 mt-2">{value}</p>
      </div>
      <div className={`bg-${color}-100 p-3 rounded-full`}>{icon}</div>
    </div>
  </div>
);

const SearchAndFilter = ({
  searchQuery,
  setSearchQuery,
  categories,
  selectedCategory,
  setSelectedCategory,
}) => (
  <div className="bg-white p-6 rounded-xl shadow-lg mb-10">
    <div className="flex flex-col lg:flex-row gap-4">
      <div className="flex-1 relative">
        <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-400" />
        <input
          type="text"
          placeholder="Search courses..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="w-full pl-12 pr-4 py-3 border rounded-lg"
        />
      </div>

      <div className="flex gap-2 overflow-x-auto">
        {categories.map((c) => (
          <button
            key={c}
            onClick={() => setSelectedCategory(c)}
            className={`px-6 py-3 rounded-lg whitespace-nowrap ${
              selectedCategory === c
                ? "bg-indigo-600 text-white shadow"
                : "bg-gray-100 hover:bg-gray-200"
            }`}
          >
            {c}
          </button>
        ))}
      </div>
    </div>
  </div>
);

const CourseGrid = ({ courses, setActiveCourse }) => (
  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
    {courses.map((course) => (
      <div
        key={course.id}
        onClick={() => setActiveCourse(course)}
        className="bg-white rounded-xl shadow hover:shadow-xl cursor-pointer overflow-hidden transition"
      >
        <img src={course.image} className="h-48 w-full object-cover" />

        <div className="p-5">
          <div className="flex justify-between mb-2">
            <span className="text-xs bg-blue-100 px-2 py-1 rounded">{course.category}</span>
            <span className="text-yellow-500">‚≠ê {course.rating}</span>
          </div>

          <h3 className="text-lg font-bold mb-1">{course.title}</h3>
          <p className="text-sm text-gray-600">By {course.instructor}</p>
        </div>
      </div>
    ))}
  </div>
);

const CourseSkeletonGrid = () => (
  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 animate-pulse">
    {[1, 2, 3, 4, 5, 6].map((n) => (
      <div key={n} className="bg-white h-64 rounded-xl shadow" />
    ))}
  </div>
);

/* ----------------------- MODAL ---------------------------- */

const CourseModal = ({ course, onClose }) => (
  <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
    <div className="bg-white rounded-xl p-6 w-[90%] md:w-[500px] shadow-xl relative">
      <button
        onClick={onClose}
        className="absolute right-3 top-3 text-gray-500 hover:text-black"
      >
        <X />
      </button>

      <img src={course.image} className="rounded-lg mb-4" />

      <h2 className="text-2xl font-bold">{course.title}</h2>
      <p className="text-gray-600 mb-3">By {course.instructor}</p>

      <button className="w-full bg-indigo-600 text-white py-3 rounded-lg hover:bg-indigo-700">
        {course.enrolled ? "Continue Learning" : "Enroll Now"}
      </button>
    </div>
  </div>
);

